import Head from 'next/head'
import { useEffect, useState } from 'react'
import styles from '../styles/Home.module.css'
import mapboxgl from 'mapbox-gl/dist/mapbox-gl.js'

// import localData from '../public/files/test.geojson'
import localData from '../public/files/polygon.geojson'
// import localData from '../public/files/last_test3.geojson'
// const localData = require('json-loader!../public/files/last_test3.geojson')


mapboxgl.accessToken = process.env.MAPBOX_TOKEN;

export default function Home() {
  const [pageIsMounted, setPageIsMounted] = useState(false)

  useEffect(() => {
    setPageIsMounted(true)
      const map = new mapboxgl.Map({
        container: 'myMap',
        style: 'mapbox://styles/mapbox/dark-v10',
        center: [-92.107970096021816, 17.870719256479013],
        zoom: 7
      });

      console.log(localData)

      // Show an alert if the browser does not support Mapbox GL
    if (!mapboxgl.supported()) {
      alert('Your browser does not support Mapbox GL');
    }

      // Add zoom and rotation controls to the map.
      map.addControl(new mapboxgl.NavigationControl());

      // Add geolocation button
      map.addControl(
        new mapboxgl.GeolocateControl({
          positionOptions: {
            enableHighAccuracy: true,
          },
          trackUserLocation: true,
        })
      );

      map.on('load', function () {
        // Add a new source from our GeoJSON data and
        // set the 'cluster' option to true. GL-JS will
        // add the point_count property to your source data.
        map.addSource('myMap', {
        type: 'geojson',
        // Point to GeoJSON data. This example visualizes all M1.0+ earthquakes
        // from 12/22/15 to 1/21/16 as logged by USGS' Earthquake hazards program.
        data: localData,
        cluster: true,
        clusterMaxZoom: 14, // Max zoom to cluster points on
        clusterRadius: 50 // Radius of each cluster when clustering points (defaults to 50)
        });

        // map.addLayer({
        //   id: 'clusters',
        //   type: 'circle',
        //   source: 'earthquakes',
        //   filter: ['has', 'point_count'],
        //   paint: {
        //   // Use step expressions (https://docs.mapbox.com/mapbox-gl-js/style-spec/#expressions-step)
        //   // with three steps to implement three types of circles:
        //   //   * Blue, 20px circles when point count is less than 100
        //   //   * Yellow, 30px circles when point count is between 100 and 750
        //   //   * Pink, 40px circles when point count is greater than or equal to 750
        //   'circle-color': [
        //   'step',
        //   ['get', 'point_count'],
        //   '#51bbd6',
        //   100,
        //   '#f1f075',
        //   750,
        //   '#f28cb1'
        //   ],
        //   'circle-radius': [
        //   'step',
        //   ['get', 'point_count'],
        //   20,
        //   100,
        //   30,
        //   750,
        //   40
        //   ]
        //   }
        //   });

        map.addLayer({
          'id': 'myMap',
          'type': 'fill',
          'source': 'myMap',
          'layout': {},
          'paint': {
          'fill-color': '#088',
          'fill-opacity': 0.8
          }
          });

        });

}, [])

  return (
    <div className={styles.container}>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
        <link href='https://api.mapbox.com/mapbox-gl-js/v2.0.0/mapbox-gl.css' rel='stylesheet' />
      </Head>

        <div style={{ width: '100%', height: '30rem' }} id="myMap">
        </div>

      <footer className={styles.footer}>

      </footer>
    </div>
  )
}
